mapper.select:
select sb.sub_title,sb.sub_code ,sb.CAT_CODE
from subject_code sb, CAT_CODE cc
where (sb.cat_code=cc.cat_code) and cc.prime_CAT_CODE=#{ subcode }

mapper.insert:
insert into subject (sub_code, sub_title, intro, goal, total_no, total_time, create_date, status, image, comp_time, inst_id
values(#{sub_code},#{sub_title},#{intro},#{goal},#{total_no},#{total_time},#{create_date},#{status},#{image},#{comp_time})
where inst_id=#{inst_id}
